import  {FETCH_ALL, CREATE, DELETE, UPDATE, LIKE} from '../actionTypes/actionTypes'


export default (state = [], action) =>{

    switch(action.type){
        case  FETCH_ALL:
            return action.payload
        case CREATE:
            return [...state, action.payload]
        default : 
            return state
    }
}
