import axios from 'axios'

const productUrl = 'http://localhost:7001/api/posts'
const userUrl = 'http://localhost:7001/api/users'

export const signup = (user) => axios.post(userUrl, user)

export const getProducts = () => axios.get(productUrl)

export const likePost = (id) => axios.patch(`${postUrl}/${id}/likePost`)
export const deletePost = (id) => axios.delete(`${postUrl}/${id}`)
export const createPost = (newPost) => axios.post(postUrl, newPost)
export const updatePost = (id, updatedPost) => axios.patch(`${postUrl}/${id}`, updatedPost)
